.mc-process {
  margin: 0 60px;
}
.mc-process .mc-process-cont {
  position: relative;
  min-height: 100px;
}
.mc-process .mc-process-wrap {
  position: absolute;
  height: 0px;
  margin: 0 auto;
  *zoom: 1;
  top: 50%;
  border-top: 1px solid #eee;
}
.mc-process .mc-process-sd {
  position: absolute;
  top: 50%;
  height: 0px;
  z-index: 1;
  -webkit-transition: width ease-in-out 1s;
  -moz-transition: width ease-in-out 1s;
  -o-transition: width ease-in-out 1s;
  transition: width ease-in-out 1s;
}
.mc-process .mc-process-i {
  position: absolute;
  top: 20px;
  width: 60px;
  height: 60px;
  text-align: center;
  margin-left: -16px;
  background-color: #fff;
  z-index: 2;
}
.mc-process .mc-process-i-c {
  line-height: 44px;
  font-size: 20px;
  font-style: normal;
  color: #fff;
  position: absolute;
  top: 8px;
  left: 8px;
  width: 44px;
  height: 44px;
  border-radius: 50%;
  background-color: #FF2555;
  z-index: 3;
}
.mc-process .mc-process-tip-tp,
.mc-process .mc-process-tip-bt {
  position: absolute;
  top: -22px;
  width: 140px;
  height: 20px;
  left: 50%;
  margin-left: -70px;
  font-size: 12px;
  font-style: normal;
  line-height: 20px;
  color: #FF2555;
}
.mc-process .mc-process-tip-bt {
  top: auto;
  bottom: -22px;
}
